(i &gt; <span class="hljs-number">5</span> ? ture : <span class="hljs-literal">false</span>);
i=<span class="hljs-number">5</span>;
<span class="hljs-keyword">if</span>(j &gt; <span class="hljs-number">8</span>)
{
